template.content:
package ${enum.packageName};

${enum.visibility} enum ${enum.name} {
    <#if enum.literals?? && (enum.literals?size>0)>
        <#assign lts>
            <#list enum.literals as t>${t.name},</#list>
        </#assign>
        ${lts?substring(0, lts?last_index_of(","))};
    </#if>

}
